mapper.insert:
INSERT INTO spaData (
    spaGroup, spaName, stateCd, hotType, hotTemp, spaAddr, spaMemo
) VALUES (
    #{spaGroup}, #{spaName}, #{stateCd}, #{hotType}, #{hotTemp}, #{spaAddr}, #{spaMemo}
)

mapper.select:
SELECT spaGroup, spaName, stateCd, hotType, hotTemp, spaAddr, spaMemo
FROM spaData
WHERE spaGroup = #{spaGroup} AND spaName = #{spaName}

SELECT spaGroup, spaName, hotType, hotTemp, spaMemo
FROM spaData
WHERE spaGroup = #{spaGroup}
